import axios from 'axios';
const axiosins=axios.create({
    baseURL:"http://localhost:8000"
});
const UserApi={
    getAll :()=>{
        return axiosins.request({
            url:"/users",
            method:"GET"
        })
    },
    getSingle:(id)=>{
        return axiosins.request({
            url:`/users/${id}`,
            method:"GET"
        })
    },
    create:(user)=>{
        return axiosins.request({
            url:`/users`,
            method :"POST",
            data:user
        })
    },
    update:(user,id)=>{
        return axios.request({
            url:`users/${id}`,
            method:"POST",
            data:user
        })
    },
    delete:(id)=>{
        return axios.request({
            url:`/users/${id}`,
            method:"DELETE"
        })
    }
};
export default UserApi
